<?php $row = 1; if (($handle = fopen("https://docs.google.com/spreadsheets/d/e/2PACX-1vSsusW7F3jOiKeBToNj6R2zzjD1h-TbZ58gtUPd74ElnwoQvbfLHYlwqVAAleQqIHbZrMkt3rrjj2jI/pub?gid=2077095330&single=true&output=csv", "r")) !== FALSE) { while (($data = fgetcsv($handle, 1000, ",")) !== FALSE) { $num = count($data); echo " ","Record #",$row," "; $row++; for ($recordcount=0; $recordcount < $num; $recordcount++) { echo $data[$recordcount] . " \n"; } } fclose($handle); } ?>